mat2 rot2d(float a){
return mat2(cos(a), sin(a), -sin(a), cos(a));
}

mat3 rot3d(float a, float b, float c){
return mat3(cos(a)*cos(b), sin(a)*sin(b), -sin(b), cos(a)*sin(b)*sin(c)-sin(a)*cos(c), sin(a)*sin(b)*sin(c)+cos(a)*cos(c), cos(b)*sin(c), cos(a)*sin(b)*cos(c)+sin(a)*sin(c), sin(a)*sin(b)*cos(c)-cos(a)*sin(c), cos(b)*cos(c));
}
